@push('script')
    <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
    <script>
        // Function to create the chart data
        function createChartData() {
            // Define the data for the chart
            const totalCountsData = {
                'Users': {{ $user }},
                'Product Categories': {{ $productcategory }},
                'Products': {{ $product }},
                'Announcements': {{ $announcement }},
                'FAQs': {{ $question }},
                'Upcoming Projects': {{ $upcomingproject }},
                'Campaigns': {{ $campaign }},
                'Subscribers': {{ $subscribe }}
            };

            // Function to generate random colors
            function dynamicColors() {
                const r = Math.floor(Math.random() * 255);
                const g = Math.floor(Math.random() * 255);
                const b = Math.floor(Math.random() * 255);
                return "rgba(" + r + "," + g + "," + b + ", 0.6)";
            }

            return {
                labels: Object.keys(totalCountsData),
                datasets: [{
                    label: 'Analytical Bar Chart',
                    data: Object.values(totalCountsData),
                    backgroundColor: Object.keys(totalCountsData).map(dynamicColors),
                    borderColor: '#fff',
                    borderWidth: 1
                }]
            };
        }

        // Function to create the chart options
        function createChartOptions() {
            return {
                responsive: true,
                maintainAspectRatio: false,
                scales: {
                    yAxes: [{
                        ticks: {
                            beginAtZero: true,
                            fontColor: '#555',
                            fontSize: 14
                        },
                        gridLines: {
                            color: 'rgba(0, 0, 0, 0.1)'
                        }
                    }],
                    xAxes: [{
                        ticks: {
                            fontColor: '#555',
                            fontSize: 14
                        },
                        gridLines: {
                            display: false
                        }
                    }]
                },
                tooltips: {
                    backgroundColor: 'rgba(0, 0, 0, 0.7)',
                    titleFontColor: '#fff',
                    bodyFontColor: '#fff',
                    displayColors: false,
                    callbacks: {
                        label: function(tooltipItem, data) {
                            const label = data.labels[tooltipItem.index] || '';
                            const value = data.datasets[tooltipItem.datasetIndex].data[tooltipItem.index];
                            return label + ': ' + value;
                        }
                    }
                },
                legend: {
                    display: false
                },
                animation: {
                    duration: 1000,
                    easing: 'easeInOutQuad'
                }
            };
        }

        // Function to create the chart
        function createChart() {
            // Get the context of the canvas element we want to select
            const ctx = document.getElementById('chartTotalCounts').getContext('2d');

            // Create the chart data and options
            const chartData = createChartData();
            const chartOptions = createChartOptions();

            // Create a new Chart object
            return new Chart(ctx, {
                type: 'bar',
                data: chartData,
                options: chartOptions
            });
        }

        // Create the chart
        const chartTotalCounts = createChart();
    </script>


    <script>
        // Chart data for line chart
        const lineChartData = {
            labels: @json(array_keys($totalCountsData)),
            datasets: [{
                label: 'Total Count',
                data: @json(array_values($totalCountsData)),
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
                borderColor: 'rgba(75, 192, 192, 1)',
                borderWidth: 2,
                pointRadius: 5,
                pointBackgroundColor: 'rgba(75, 192, 192, 1)',
                pointBorderColor: '#fff',
                pointHoverRadius: 8,
                pointHoverBackgroundColor: '#fff',
                pointHoverBorderColor: 'rgba(75, 192, 192, 1)'
            }]
        };

        // Chart options for line chart
        const lineChartOptions = {
            responsive: true,
            maintainAspectRatio: false,
            title: {
                display: true,
                text: 'Total Counts Over Time',
                fontSize: 20,
                fontColor: '#333'
            },
            scales: {
                yAxes: [{
                    ticks: {
                        beginAtZero: true,
                        fontColor: '#555',
                        fontSize: 14
                    },
                    gridLines: {
                        color: 'rgba(0, 0, 0, 0.1)'
                    }
                }],
                xAxes: [{
                    ticks: {
                        fontColor: '#555',
                        fontSize: 14
                    },
                    gridLines: {
                        display: false
                    }
                }]
            },
            tooltips: {
                backgroundColor: 'rgba(0, 0, 0, 0.7)',
                titleFontColor: '#fff',
                bodyFontColor: '#fff',
                displayColors: false,
                callbacks: {
                    label: function(tooltipItem, data) {
                        const label = data.labels[tooltipItem.index] || '';
                        const value = data.datasets[tooltipItem.datasetIndex].data[tooltipItem.index];
                        return label + ': ' + value;
                    }
                }
            },
            hover: {
                mode: 'nearest',
                intersect: true
            },
            legend: {
                display: false
            },
            animation: {
                duration: 1000,
                easing: 'easeInOutQuad'
            }
        };

        // Create line chart
        const lineCtx = document.getElementById('lineChartTotalCounts').getContext('2d');
        const lineChartTotalCounts = new Chart(lineCtx, {
            type: 'line',
            data: lineChartData,
            options: lineChartOptions
        });
    </script>
